contracts/ClawCardNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title ClawCardNFT
 * @notice Dynamic NFT representing an agent's ClawTrust reputation card.
 *         ONE CARD PER WALLET - Transfers are restricted to maintain this invariant.
 *         Token metadata is served dynamically via the ClawTrust API.
 *         Supports soulbound (non-transferable) mode per token.
 */
contract ClawCardNFT is ERC721, Ownable {
    using Strings for uint256;

    uint256 private _nextTokenId;
    string public baseTokenURI;
    uint256 public constant MAX_SUPPLY = 1_000_000;

    mapping(address => uint256) public walletToToken;
    mapping(address => bool) public hasMinted;
    mapping(uint256 => bool) public soulbound;
    mapping(uint256 => string) public tokenAgentId;
    mapping(string => bool) public agentIdUsed;
    mapping(string => uint256) public agentIdToToken;

    bool public transfersEnabled = true;

    event CardMinted(address indexed wallet, uint256 indexed tokenId, string agentId, bool isSoulbound);
    event CardBurned(address indexed wallet, uint256 indexed tokenId, string agentId);
    event SoulboundLocked(uint256 indexed tokenId);
    event BaseURIUpdated(string newBaseURI);
    event AgentIdUpdated(uint256 indexed tokenId, string oldAgentId, string newAgentId);
    event TransfersToggled(bool enabled);

    error AlreadyMinted();
    error InvalidAgentId();
    error AgentIdInUse();
    error NotTokenOwner();
    error TokenIsSoulbound();
    error TransfersDisabled();
    error MaxSupplyReached();
    error InvalidBaseURI();
    error TokenDoesNotExist();

    modifier onlyTokenOwner(uint256 tokenId) {
        if(ownerOf(tokenId) != msg.sender) revert NotTokenOwner();
        _;
    }

    constructor(
        string memory _baseTokenURI
    ) ERC721("ClawTrust Card", "CLAW") Ownable(msg.sender) {
        if(bytes(_baseTokenURI).length == 0) revert InvalidBaseURI();
        baseTokenURI = _baseTokenURI;
        _nextTokenId = 1;
    }

    function mint(string calldata agentId, bool makeSoulbound) external {
        _mintCard(msg.sender, agentId, makeSoulbound);
    }

    function adminMint(
        address to,
        string calldata agentId,
        bool makeSoulbound
    ) external onlyOwner {
        _mintCard(to, agentId, makeSoulbound);
    }

    function _mintCard(
        address to,
        string calldata agentId,
        bool makeSoulbound
    ) internal {
        if(hasMinted[to]) revert AlreadyMinted();
        if(bytes(agentId).length == 0) revert InvalidAgentId();
        if(agentIdUsed[agentId]) revert AgentIdInUse();
        if(_nextTokenId > MAX_SUPPLY) revert MaxSupplyReached();

        uint256 tokenId = _nextTokenId++;
        _safeMint(to, tokenId);

        walletToToken[to] = tokenId;
        hasMinted[to] = true;
        tokenAgentId[tokenId] = agentId;
        agentIdUsed[agentId] = true;
        agentIdToToken[agentId] = tokenId;

        if (makeSoulbound) {
            soulbound[tokenId] = true;
        }

        emit CardMinted(to, tokenId, agentId, makeSoulbound);
    }

    function burn(uint256 tokenId) external onlyTokenOwner(tokenId) {
        address tokenOwner = ownerOf(tokenId);
        string memory agentId = tokenAgentId[tokenId];

        delete walletToToken[tokenOwner];
        delete hasMinted[tokenOwner];
        delete soulbound[tokenId];
        delete tokenAgentId[tokenId];
        delete agentIdUsed[agentId];
        delete agentIdToToken[agentId];

        _burn(tokenId);

        emit CardBurned(tokenOwner, tokenId, agentId);
    }

    function lockAsSoulbound(uint256 tokenId) external onlyTokenOwner(tokenId) {
        if(soulbound[tokenId]) return;

        _approve(address(0), tokenId, msg.sender);

        soulbound[tokenId] = true;
        emit SoulboundLocked(tokenId);
    }

    function updateAgentId(
        uint256 tokenId,
        string calldata newAgentId
    ) external onlyTokenOwner(tokenId) {
        if(soulbound[tokenId]) revert TokenIsSoulbound();
        if(bytes(newAgentId).length == 0) revert InvalidAgentId();
        if(agentIdUsed[newAgentId]) revert AgentIdInUse();

        string memory oldAgentId = tokenAgentId[tokenId];

        delete agentIdUsed[oldAgentId];
        delete agentIdToToken[oldAgentId];

        tokenAgentId[tokenId] = newAgentId;
        agentIdUsed[newAgentId] = true;
        agentIdToToken[newAgentId] = tokenId;

        emit AgentIdUpdated(tokenId, oldAgentId, newAgentId);
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        _requireOwned(tokenId);
        string memory agentId = tokenAgentId[tokenId];

        return string.concat(
            baseTokenURI,
            "/api/agents/",
            agentId,
            "/card/metadata"
        );
    }

    function setBaseURI(string calldata newBaseURI) external onlyOwner {
        if(bytes(newBaseURI).length == 0) revert InvalidBaseURI();
        baseTokenURI = newBaseURI;
        emit BaseURIUpdated(newBaseURI);
    }

    function setTransfersEnabled(bool enabled) external onlyOwner {
        transfersEnabled = enabled;
        emit TransfersToggled(enabled);
    }

    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override returns (address) {
        address from = _ownerOf(tokenId);

        if (from != address(0) && to != address(0)) {
            if(soulbound[tokenId]) revert TokenIsSoulbound();
            if(!transfersEnabled) revert TransfersDisabled();
            if(hasMinted[to]) revert AlreadyMinted();

            delete walletToToken[from];
            delete hasMinted[from];

            walletToToken[to] = tokenId;
            hasMinted[to] = true;
        }

        return super._update(to, tokenId, auth);
    }

    function approve(address to, uint256 tokenId) public override {
        if(soulbound[tokenId]) revert TokenIsSoulbound();
        super.approve(to, tokenId);
    }

    function totalSupply() external view returns (uint256) {
        return _nextTokenId - 1;
    }

    function getTokenByAgentId(string calldata agentId) external view returns (uint256) {
        if(!agentIdUsed[agentId]) revert InvalidAgentId();
        return agentIdToToken[agentId];
    }

    function isAgentIdAvailable(string calldata agentId) external view returns (bool) {
        return !agentIdUsed[agentId];
    }

    function getCardInfo(uint256 tokenId) external view returns (
        address cardOwner,
        string memory agentId,
        bool isSoulbound,
        bool transferable
    ) {
        cardOwner = ownerOf(tokenId);
        agentId = tokenAgentId[tokenId];
        isSoulbound = soulbound[tokenId];
        transferable = !isSoulbound && transfersEnabled;
    }
}

contracts/ClawTrustEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ClawTrustEscrow is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    enum EscrowStatus { Pending, Locked, Released, Refunded, Disputed }

    struct Escrow {
        bytes32 gigId;
        address depositor;
        address payee;
        uint256 amount;
        address token;
        EscrowStatus status;
        uint256 createdAt;
        uint256 resolvedAt;
    }

    mapping(bytes32 => Escrow) public escrows;
    mapping(bytes32 => bool) public escrowExists;
    mapping(address => bool) public approvedTokens;

    address public immutable validationRegistry;
    uint256 public platformFeeRate;
    uint256 public constant FEE_DENOMINATOR = 10000;
    uint256 public constant MAX_FEE_RATE = 1000;
    uint256 public constant ESCROW_TIMEOUT = 90 days;

    event EscrowCreated(bytes32 indexed gigId, address indexed depositor, uint256 amount, address token);
    event EscrowLocked(bytes32 indexed gigId);
    event EscrowReleased(bytes32 indexed gigId, address indexed payee, uint256 amount, uint256 fee);
    event EscrowRefunded(bytes32 indexed gigId, address indexed depositor, uint256 amount);
    event EscrowDisputed(bytes32 indexed gigId);
    event PlatformFeeRateUpdated(uint256 oldRate, uint256 newRate);
    event TokenApprovalUpdated(address indexed token, bool approved);

    error InvalidGigId();
    error EscrowAlreadyExists();
    error InvalidAmount();
    error InvalidAddress();
    error EscrowNotFound();
    error InvalidStatus();
    error Unauthorized();
    error TransferFailed();
    error SwarmNotApproved();
    error FeeTooHigh();
    error TokenNotApproved();

    constructor(address _validationRegistry, uint256 _platformFeeRate) Ownable(msg.sender) {
        if(_validationRegistry == address(0)) revert InvalidAddress();
        if(_platformFeeRate > MAX_FEE_RATE) revert FeeTooHigh();

        validationRegistry = _validationRegistry;
        platformFeeRate = _platformFeeRate;
    }

    function lockETH(bytes32 gigId, address payee) external payable nonReentrant {
        if(gigId == bytes32(0)) revert InvalidGigId();
        if(escrowExists[gigId]) revert EscrowAlreadyExists();
        if(msg.value == 0) revert InvalidAmount();
        if(payee == address(0)) revert InvalidAddress();

        escrows[gigId] = Escrow({
            gigId: gigId,
            depositor: msg.sender,
            payee: payee,
            amount: msg.value,
            token: address(0),
            status: EscrowStatus.Locked,
            createdAt: block.timestamp,
            resolvedAt: 0
        });
        escrowExists[gigId] = true;

        emit EscrowCreated(gigId, msg.sender, msg.value, address(0));
        emit EscrowLocked(gigId);
    }

    function lockERC20(bytes32 gigId, address payee, address token, uint256 amount) external nonReentrant {
        if(gigId == bytes32(0)) revert InvalidGigId();
        if(escrowExists[gigId]) revert EscrowAlreadyExists();
        if(amount == 0) revert InvalidAmount();
        if(payee == address(0) || token == address(0)) revert InvalidAddress();
        if(!approvedTokens[token]) revert TokenNotApproved();

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        escrows[gigId] = Escrow({
            gigId: gigId,
            depositor: msg.sender,
            payee: payee,
            amount: amount,
            token: token,
            status: EscrowStatus.Locked,
            createdAt: block.timestamp,
            resolvedAt: 0
        });
        escrowExists[gigId] = true;

        emit EscrowCreated(gigId, msg.sender, amount, token);
        emit EscrowLocked(gigId);
    }

    function release(bytes32 gigId) external nonReentrant {
        Escrow storage escrow = escrows[gigId];
        if(!escrowExists[gigId]) revert EscrowNotFound();
        if(escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if(msg.sender != owner() && msg.sender != escrow.depositor) revert Unauthorized();

        _releaseEscrow(escrow);
    }

    function refund(bytes32 gigId) external nonReentrant {
        Escrow storage escrow = escrows[gigId];
        if(!escrowExists[gigId]) revert EscrowNotFound();
        if(escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if(msg.sender != owner() && msg.sender != escrow.depositor) revert Unauthorized();

        escrow.status = EscrowStatus.Refunded;
        escrow.resolvedAt = block.timestamp;

        if (escrow.token == address(0)) {
            (bool sent, ) = escrow.depositor.call{value: escrow.amount}("");
            if(!sent) revert TransferFailed();
        } else {
            IERC20(escrow.token).safeTransfer(escrow.depositor, escrow.amount);
        }

        emit EscrowRefunded(gigId, escrow.depositor, escrow.amount);
    }

    function refundAfterTimeout(bytes32 gigId) external nonReentrant {
        Escrow storage escrow = escrows[gigId];
        if(!escrowExists[gigId]) revert EscrowNotFound();
        if(escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if(block.timestamp < escrow.createdAt + ESCROW_TIMEOUT) revert Unauthorized();

        escrow.status = EscrowStatus.Refunded;
        escrow.resolvedAt = block.timestamp;

        if (escrow.token == address(0)) {
            (bool sent, ) = escrow.depositor.call{value: escrow.amount}("");
            if(!sent) revert TransferFailed();
        } else {
            IERC20(escrow.token).safeTransfer(escrow.depositor, escrow.amount);
        }

        emit EscrowRefunded(gigId, escrow.depositor, escrow.amount);
    }

    function dispute(bytes32 gigId) external {
        Escrow storage escrow = escrows[gigId];
        if(!escrowExists[gigId]) revert EscrowNotFound();
        if(escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if(msg.sender != escrow.depositor && msg.sender != escrow.payee) revert Unauthorized();

        escrow.status = EscrowStatus.Disputed;
        emit EscrowDisputed(gigId);
    }

    error ValidationExpired();

    function releaseOnSwarmApproval(bytes32 gigId) external nonReentrant {
        Escrow storage escrow = escrows[gigId];
        if(!escrowExists[gigId]) revert EscrowNotFound();
        if(escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if(msg.sender != escrow.depositor && msg.sender != escrow.payee && msg.sender != owner()) {
            revert Unauthorized();
        }

        (uint256 votesFor, , uint256 threshold, uint8 status, bool isApproved) =
            ISwarmValidator(validationRegistry).aggregateVotes(gigId);

        if(!isApproved || votesFor < threshold) revert SwarmNotApproved();
        if(status == 3) revert ValidationExpired();

        _releaseEscrow(escrow);
    }

    function _releaseEscrow(Escrow storage escrow) private {
        escrow.status = EscrowStatus.Released;
        escrow.resolvedAt = block.timestamp;

        uint256 fee = (escrow.amount * platformFeeRate) / FEE_DENOMINATOR;
        uint256 payout = escrow.amount - fee;

        if (escrow.token == address(0)) {
            (bool sent, ) = escrow.payee.call{value: payout}("");
            if(!sent) revert TransferFailed();
            if (fee > 0) {
                (bool feeSent, ) = owner().call{value: fee}("");
                if(!feeSent) revert TransferFailed();
            }
        } else {
            IERC20(escrow.token).safeTransfer(escrow.payee, payout);
            if (fee > 0) {
                IERC20(escrow.token).safeTransfer(owner(), fee);
            }
        }

        emit EscrowReleased(escrow.gigId, escrow.payee, payout, fee);
    }

    function setPlatformFeeRate(uint256 _rate) external onlyOwner {
        if(_rate > MAX_FEE_RATE) revert FeeTooHigh();
        uint256 oldRate = platformFeeRate;
        platformFeeRate = _rate;
        emit PlatformFeeRateUpdated(oldRate, _rate);
    }

    function setTokenApproval(address token, bool approved) external onlyOwner {
        if(token == address(0)) revert InvalidAddress();
        approvedTokens[token] = approved;
        emit TokenApprovalUpdated(token, approved);
    }

    function getEscrow(bytes32 gigId) external view returns (Escrow memory) {
        if(!escrowExists[gigId]) revert EscrowNotFound();
        return escrows[gigId];
    }
}

interface ISwarmValidator {
    function aggregateVotes(bytes32 gigId) external view returns (
        uint256 votesFor,
        uint256 votesAgainst,
        uint256 threshold,
        uint8 status,
        bool isApproved
    );
}

contracts/ClawTrustRepAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/IERC8004Reputation.sol";

contract ClawTrustRepAdapter is Ownable, Pausable, ReentrancyGuard {
    uint256 public constant ON_CHAIN_WEIGHT = 60;
    uint256 public constant MOLTBOOK_WEIGHT = 40;
    uint256 public constant WEIGHT_DENOMINATOR = 100;
    uint256 public constant MAX_ON_CHAIN_SCORE = 1000;
    uint256 public constant MAX_MOLTBOOK_KARMA = 10000;
    uint256 public constant UPDATE_COOLDOWN = 1 hours;
    uint256 public constant MAX_SCORE = 100;

    struct FusedScore {
        uint256 onChainScore;
        uint256 moltbookKarma;
        uint256 fusedScore;
        uint256 timestamp;
        bytes32 proofHash;
    }

    struct ScoreHistory {
        uint256 fusedScore;
        uint256 timestamp;
    }

    mapping(address => FusedScore) public fusedScores;
    mapping(address => ScoreHistory[]) public scoreHistory;
    mapping(address => bool) public authorizedOracles;
    mapping(address => uint256) public lastUpdateTime;

    address public immutable reputationRegistry;
    uint256 public minOracleCount = 1;
    uint256 public oracleCount;

    event FusedScoreUpdated(
        address indexed agent,
        uint256 fusedScore,
        uint256 onChainScore,
        uint256 moltbookKarma,
        bytes32 proofHash
    );
    event FeedbackSubmitted(
        address indexed oracle,
        address indexed to,
        int256 score,
        bytes32 proofHash
    );
    event OracleAuthorized(address indexed oracle);
    event OracleRevoked(address indexed oracle);
    event ReputationRegistryCallFailed(address indexed agent, bytes reason);
    event MinOracleCountUpdated(uint256 oldCount, uint256 newCount);

    error InvalidAddress();
    error InvalidScore();
    error InvalidProof();
    error UpdateTooSoon();
    error NotAuthorizedOracle();
    error InsufficientOracles();
    error ScoreOutOfBounds();

    modifier onlyOracle() {
        if(!authorizedOracles[msg.sender]) revert NotAuthorizedOracle();
        _;
    }

    modifier rateLimited(address agent) {
        if(block.timestamp < lastUpdateTime[agent] + UPDATE_COOLDOWN) {
            revert UpdateTooSoon();
        }
        _;
        lastUpdateTime[agent] = block.timestamp;
    }

    constructor(address _reputationRegistry) Ownable(msg.sender) {
        if(_reputationRegistry == address(0)) revert InvalidAddress();
        reputationRegistry = _reputationRegistry;
    }

    function computeFusedScore(
        uint256 onChainScore,
        uint256 moltbookKarma
    ) public pure returns (uint256) {
        if(onChainScore > MAX_ON_CHAIN_SCORE) revert ScoreOutOfBounds();
        if(moltbookKarma > MAX_MOLTBOOK_KARMA) revert ScoreOutOfBounds();

        uint256 normalizedOnChain = (onChainScore * 100) / MAX_ON_CHAIN_SCORE;
        uint256 normalizedMoltbook = (moltbookKarma * 100) / MAX_MOLTBOOK_KARMA;

        uint256 fused = (ON_CHAIN_WEIGHT * normalizedOnChain + MOLTBOOK_WEIGHT * normalizedMoltbook)
                        / WEIGHT_DENOMINATOR;

        assert(fused <= MAX_SCORE);

        return fused;
    }

    function updateFusedScore(
        address agent,
        uint256 onChainScore,
        uint256 moltbookKarma,
        string calldata proofUri
    ) external onlyOracle whenNotPaused rateLimited(agent) {
        if(agent == address(0)) revert InvalidAddress();
        if(bytes(proofUri).length == 0) revert InvalidProof();

        uint256 fused = computeFusedScore(onChainScore, moltbookKarma);
        bytes32 proofHash = keccak256(bytes(proofUri));

        fusedScores[agent] = FusedScore({
            onChainScore: onChainScore,
            moltbookKarma: moltbookKarma,
            fusedScore: fused,
            timestamp: block.timestamp,
            proofHash: proofHash
        });

        scoreHistory[agent].push(ScoreHistory({
            fusedScore: fused,
            timestamp: block.timestamp
        }));

        emit FusedScoreUpdated(agent, fused, onChainScore, moltbookKarma, proofHash);
    }

    function updateFusedScoreBatch(
        address[] calldata agents,
        uint256[] calldata onChainScores,
        uint256[] calldata moltbookKarmas,
        string[] calldata proofUris
    ) external onlyOracle whenNotPaused {
        uint256 length = agents.length;
        if(length != onChainScores.length ||
           length != moltbookKarmas.length ||
           length != proofUris.length) {
            revert InvalidScore();
        }

        for(uint256 i = 0; i < length; i++) {
            address agent = agents[i];
            if(agent == address(0)) revert InvalidAddress();
            if(block.timestamp < lastUpdateTime[agent] + UPDATE_COOLDOWN) continue;

            uint256 fused = computeFusedScore(onChainScores[i], moltbookKarmas[i]);
            bytes32 proofHash = keccak256(bytes(proofUris[i]));

            fusedScores[agent] = FusedScore({
                onChainScore: onChainScores[i],
                moltbookKarma: moltbookKarmas[i],
                fusedScore: fused,
                timestamp: block.timestamp,
                proofHash: proofHash
            });

            scoreHistory[agent].push(ScoreHistory({
                fusedScore: fused,
                timestamp: block.timestamp
            }));

            lastUpdateTime[agent] = block.timestamp;

            emit FusedScoreUpdated(agent, fused, onChainScores[i], moltbookKarmas[i], proofHash);
        }
    }

    function submitFeedbackToRegistry(
        address to,
        int256 score,
        string[] calldata tags,
        string calldata proofUri
    ) external onlyOracle whenNotPaused nonReentrant {
        if(to == address(0)) revert InvalidAddress();
        if(bytes(proofUri).length == 0) revert InvalidProof();

        bytes32 proofHash = keccak256(bytes(proofUri));

        try IERC8004Reputation(reputationRegistry).submitFeedback(
            to,
            score,
            tags,
            proofUri
        ) {
        } catch (bytes memory reason) {
            emit ReputationRegistryCallFailed(to, reason);
        }

        emit FeedbackSubmitted(msg.sender, to, score, proofHash);
    }

    function submitFusedFeedback(
        address agentAddress,
        uint256 onChainScore,
        uint256 moltbookKarma,
        string[] calldata tags,
        string calldata proofUri
    ) external onlyOracle whenNotPaused rateLimited(agentAddress) nonReentrant {
        if(agentAddress == address(0)) revert InvalidAddress();
        if(bytes(proofUri).length == 0) revert InvalidProof();

        uint256 fused = computeFusedScore(onChainScore, moltbookKarma);
        bytes32 proofHash = keccak256(bytes(proofUri));

        fusedScores[agentAddress] = FusedScore({
            onChainScore: onChainScore,
            moltbookKarma: moltbookKarma,
            fusedScore: fused,
            timestamp: block.timestamp,
            proofHash: proofHash
        });

        scoreHistory[agentAddress].push(ScoreHistory({
            fusedScore: fused,
            timestamp: block.timestamp
        }));

        emit FusedScoreUpdated(agentAddress, fused, onChainScore, moltbookKarma, proofHash);

        int256 signedFused = int256(fused);

        try IERC8004Reputation(reputationRegistry).submitFeedback(
            agentAddress,
            signedFused,
            tags,
            proofUri
        ) {
        } catch (bytes memory reason) {
            emit ReputationRegistryCallFailed(agentAddress, reason);
        }

        emit FeedbackSubmitted(msg.sender, agentAddress, signedFused, proofHash);
    }

    function getFusedScore(address agent) external view returns (FusedScore memory) {
        return fusedScores[agent];
    }

    function getScoreHistory(
        address agent,
        uint256 offset,
        uint256 limit
    ) external view returns (ScoreHistory[] memory) {
        ScoreHistory[] storage history = scoreHistory[agent];
        uint256 total = history.length;

        if(offset >= total) {
            return new ScoreHistory[](0);
        }

        uint256 end = offset + limit;
        if(end > total) {
            end = total;
        }

        uint256 size = end - offset;
        ScoreHistory[] memory result = new ScoreHistory[](size);

        for(uint256 i = 0; i < size; i++) {
            result[i] = history[offset + i];
        }

        return result;
    }

    function authorizeOracle(address oracle) external onlyOwner {
        if(oracle == address(0)) revert InvalidAddress();
        if(!authorizedOracles[oracle]) {
            authorizedOracles[oracle] = true;
            oracleCount++;
            emit OracleAuthorized(oracle);
        }
    }

    function revokeOracle(address oracle) external onlyOwner {
        if(authorizedOracles[oracle]) {
            if(oracleCount <= minOracleCount) revert InsufficientOracles();
            authorizedOracles[oracle] = false;
            oracleCount--;
            emit OracleRevoked(oracle);
        }
    }

    function setMinOracleCount(uint256 _minCount) external onlyOwner {
        if(_minCount == 0) revert InvalidScore();
        if(_minCount > oracleCount) revert InsufficientOracles();
        uint256 oldCount = minOracleCount;
        minOracleCount = _minCount;
        emit MinOracleCountUpdated(oldCount, _minCount);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function verifyProof(address agent, string calldata proofUri) external view returns (bool) {
        bytes32 proofHash = keccak256(bytes(proofUri));
        return fusedScores[agent].proofHash == proofHash;
    }
}

contracts/ClawTrustSwarmValidator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract ClawTrustSwarmValidator is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    enum VoteType { None, Approve, Reject }
    enum ValidationStatus { Pending, Approved, Rejected, Expired }

    struct ValidationRequest {
        bytes32 gigId;
        address[] candidates;
        mapping(address => VoteType) votes;
        mapping(address => bool) isCandidate;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 threshold;
        ValidationStatus status;
        uint256 createdAt;
        uint256 resolvedAt;
        uint256 expiresAt;
        uint256 rewardPool;
        uint256 rewardPoolClaimed;
        address rewardToken;
        mapping(address => bool) rewardClaimed;
    }

    struct ValidationInfo {
        bytes32 gigId;
        address[] candidates;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 threshold;
        ValidationStatus status;
        uint256 createdAt;
        uint256 resolvedAt;
        uint256 expiresAt;
        uint256 rewardPool;
        address rewardToken;
    }

    mapping(bytes32 => ValidationRequest) internal validations;
    mapping(bytes32 => bool) public validationExists;

    address public escrowContract;
    uint256 public constant MAX_CANDIDATES = 50;
    uint256 public constant VALIDATION_DURATION = 7 days;
    uint256 public defaultThreshold = 3;
    uint256 public defaultCandidateCount = 5;

    event ValidationCreated(
        bytes32 indexed gigId,
        address[] candidates,
        uint256 threshold,
        uint256 rewardPool,
        address rewardToken,
        uint256 expiresAt
    );
    event VoteCast(bytes32 indexed gigId, address indexed voter, VoteType vote);
    event ValidationResolved(
        bytes32 indexed gigId,
        ValidationStatus status,
        uint256 votesFor,
        uint256 votesAgainst
    );
    event RewardClaimed(bytes32 indexed gigId, address indexed validator, uint256 amount);
    event ValidationExpired(bytes32 indexed gigId);
    event EscrowContractUpdated(address indexed oldEscrow, address indexed newEscrow);
    event DefaultThresholdUpdated(uint256 oldThreshold, uint256 newThreshold);

    error ValidationAlreadyExists();
    error InsufficientCandidates();
    error InvalidThreshold();
    error ValidationNotFound();
    error ValidationAlreadyResolved();
    error InvalidVote();
    error AlreadyVoted();
    error NotCandidate();
    error TooManyCandidates();
    error DuplicateCandidate();
    error InvalidAddress();
    error NoRewardAvailable();
    error RewardAlreadyClaimed();
    error InsufficientRewardPool();
    error ValidationNotApproved();
    error TransferFailed();
    error NotExpired();

    modifier onlyEscrow() {
        if(msg.sender != escrowContract) revert InvalidAddress();
        _;
    }

    constructor(address _escrowContract) Ownable(msg.sender) {
        if(_escrowContract == address(0)) revert InvalidAddress();
        escrowContract = _escrowContract;
    }

    function createValidation(
        bytes32 gigId,
        address[] calldata candidates,
        uint256 threshold,
        uint256 rewardPool,
        address rewardToken
    ) external payable onlyEscrow {
        if(validationExists[gigId]) revert ValidationAlreadyExists();
        if(candidates.length > MAX_CANDIDATES) revert TooManyCandidates();
        if(candidates.length < threshold) revert InsufficientCandidates();
        if(threshold == 0) revert InvalidThreshold();

        if(rewardToken == address(0)) {
            if(msg.value < rewardPool) revert InsufficientRewardPool();
        } else {
            if(rewardPool > 0) {
                IERC20(rewardToken).safeTransferFrom(msg.sender, address(this), rewardPool);
            }
        }

        ValidationRequest storage v = validations[gigId];
        v.gigId = gigId;
        v.threshold = threshold;
        v.status = ValidationStatus.Pending;
        v.createdAt = block.timestamp;
        v.expiresAt = block.timestamp + VALIDATION_DURATION;
        v.rewardPool = rewardPool;
        v.rewardToken = rewardToken;

        for (uint256 i = 0; i < candidates.length; i++) {
            address candidate = candidates[i];
            if(candidate == address(0)) revert InvalidAddress();
            if(v.isCandidate[candidate]) revert DuplicateCandidate();

            v.candidates.push(candidate);
            v.isCandidate[candidate] = true;
        }

        validationExists[gigId] = true;

        emit ValidationCreated(gigId, candidates, threshold, rewardPool, rewardToken, v.expiresAt);
    }

    function vote(bytes32 gigId, VoteType _vote) external {
        if(!validationExists[gigId]) revert ValidationNotFound();
        ValidationRequest storage v = validations[gigId];

        if(v.status != ValidationStatus.Pending) revert ValidationAlreadyResolved();
        if(block.timestamp >= v.expiresAt) {
            _expireValidation(gigId);
            revert ValidationAlreadyResolved();
        }
        if(_vote != VoteType.Approve && _vote != VoteType.Reject) revert InvalidVote();
        if(v.votes[msg.sender] != VoteType.None) revert AlreadyVoted();
        if(!v.isCandidate[msg.sender]) revert NotCandidate();

        v.votes[msg.sender] = _vote;

        if (_vote == VoteType.Approve) {
            v.votesFor++;
        } else {
            v.votesAgainst++;
        }

        emit VoteCast(gigId, msg.sender, _vote);

        _checkThreshold(gigId);
    }

    function _checkThreshold(bytes32 gigId) internal {
        ValidationRequest storage v = validations[gigId];

        if (v.votesFor >= v.threshold) {
            v.status = ValidationStatus.Approved;
            v.resolvedAt = block.timestamp;
            emit ValidationResolved(gigId, ValidationStatus.Approved, v.votesFor, v.votesAgainst);
        } else if (v.votesAgainst >= v.threshold) {
            v.status = ValidationStatus.Rejected;
            v.resolvedAt = block.timestamp;
            emit ValidationResolved(gigId, ValidationStatus.Rejected, v.votesFor, v.votesAgainst);
            _refundRewardPool(gigId);
        }
    }

    function _expireValidation(bytes32 gigId) internal {
        ValidationRequest storage v = validations[gigId];
        v.status = ValidationStatus.Expired;
        v.resolvedAt = block.timestamp;
        emit ValidationExpired(gigId);
        _refundRewardPool(gigId);
    }

    function expireValidation(bytes32 gigId) external {
        if(!validationExists[gigId]) revert ValidationNotFound();
        ValidationRequest storage v = validations[gigId];
        if(v.status != ValidationStatus.Pending) revert ValidationAlreadyResolved();
        if(block.timestamp < v.expiresAt) revert NotExpired();

        _expireValidation(gigId);
    }

    function _refundRewardPool(bytes32 gigId) internal {
        ValidationRequest storage v = validations[gigId];
        if(v.rewardPool == 0) return;

        uint256 amount = v.rewardPool - v.rewardPoolClaimed;
        if(amount == 0) return;
        v.rewardPoolClaimed = v.rewardPool;

        if(v.rewardToken == address(0)) {
            (bool success, ) = escrowContract.call{value: amount}("");
            if(!success) revert TransferFailed();
        } else {
            IERC20(v.rewardToken).safeTransfer(escrowContract, amount);
        }
    }

    function claimReward(bytes32 gigId) external nonReentrant {
        if(!validationExists[gigId]) revert ValidationNotFound();
        ValidationRequest storage v = validations[gigId];

        if(v.status != ValidationStatus.Approved) revert ValidationNotApproved();
        if(v.votes[msg.sender] != VoteType.Approve) revert NoRewardAvailable();
        if(v.rewardClaimed[msg.sender]) revert RewardAlreadyClaimed();
        if(v.rewardPool == 0 || v.votesFor == 0) revert NoRewardAvailable();

        uint256 rewardPerValidator = v.rewardPool / v.votesFor;
        if(rewardPerValidator == 0) revert NoRewardAvailable();

        uint256 remaining = v.rewardPool - v.rewardPoolClaimed;
        if(remaining == 0) revert NoRewardAvailable();
        if(rewardPerValidator > remaining) {
            rewardPerValidator = remaining;
        }

        v.rewardClaimed[msg.sender] = true;
        v.rewardPoolClaimed += rewardPerValidator;

        if(v.rewardToken == address(0)) {
            (bool success, ) = msg.sender.call{value: rewardPerValidator}("");
            if(!success) revert TransferFailed();
        } else {
            IERC20(v.rewardToken).safeTransfer(msg.sender, rewardPerValidator);
        }

        emit RewardClaimed(gigId, msg.sender, rewardPerValidator);
    }

    function aggregateVotes(bytes32 gigId) external view returns (
        uint256 votesFor,
        uint256 votesAgainst,
        uint256 threshold,
        uint8 status,
        bool isApproved
    ) {
        if(!validationExists[gigId]) revert ValidationNotFound();
        ValidationRequest storage v = validations[gigId];
        return (
            v.votesFor,
            v.votesAgainst,
            v.threshold,
            uint8(v.status),
            v.status == ValidationStatus.Approved
        );
    }

    function getValidationInfo(bytes32 gigId) external view returns (ValidationInfo memory) {
        if(!validationExists[gigId]) revert ValidationNotFound();
        ValidationRequest storage v = validations[gigId];
        return ValidationInfo({
            gigId: v.gigId,
            candidates: v.candidates,
            votesFor: v.votesFor,
            votesAgainst: v.votesAgainst,
            threshold: v.threshold,
            status: v.status,
            createdAt: v.createdAt,
            resolvedAt: v.resolvedAt,
            expiresAt: v.expiresAt,
            rewardPool: v.rewardPool,
            rewardToken: v.rewardToken
        });
    }

    function hasVoted(bytes32 gigId, address voter) external view returns (bool) {
        if(!validationExists[gigId]) revert ValidationNotFound();
        return validations[gigId].votes[voter] != VoteType.None;
    }

    function getVote(bytes32 gigId, address voter) external view returns (VoteType) {
        if(!validationExists[gigId]) revert ValidationNotFound();
        return validations[gigId].votes[voter];
    }

    function isCandidate(bytes32 gigId, address addr) external view returns (bool) {
        if(!validationExists[gigId]) revert ValidationNotFound();
        return validations[gigId].isCandidate[addr];
    }

    function setDefaultThreshold(uint256 _threshold) external onlyOwner {
        if(_threshold == 0 || _threshold > 20) revert InvalidThreshold();
        uint256 oldThreshold = defaultThreshold;
        defaultThreshold = _threshold;
        emit DefaultThresholdUpdated(oldThreshold, _threshold);
    }

    function setDefaultCandidateCount(uint256 _count) external onlyOwner {
        if(_count < 3 || _count > MAX_CANDIDATES) revert InvalidThreshold();
        defaultCandidateCount = _count;
    }

    function setEscrowContract(address _escrow) external onlyOwner {
        if(_escrow == address(0)) revert InvalidAddress();
        address oldEscrow = escrowContract;
        escrowContract = _escrow;
        emit EscrowContractUpdated(oldEscrow, _escrow);
    }

    function computeRewardPool(uint256 gigBudget, uint256 rewardRate, uint256 denominator) external pure returns (uint256) {
        return (gigBudget * rewardRate) / denominator;
    }

    receive() external payable {}
}

