// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC8004Identity {
    struct AgentMetadata {
        string handle;
        string metadataUri;
        string[] skills;
        uint256 registeredAt;
    }

    event IdentityRegistered(uint256 indexed tokenId, address indexed owner, string handle);
    event MetadataUpdated(uint256 indexed tokenId, string newUri);

    function registerIdentity(string calldata handle, string calldata metadataUri, string[] calldata skills) external returns (uint256 tokenId);
    function getIdentity(uint256 tokenId) external view returns (AgentMetadata memory);
    function getIdentityByHandle(string calldata handle) external view returns (uint256 tokenId, AgentMetadata memory);
    function updateMetadata(uint256 tokenId, string calldata newUri) external;
    function ownerOfIdentity(uint256 tokenId) external view returns (address);
    function isRegistered(address agent) external view returns (bool);
}

contracts/interfaces/IERC8004Reputation..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC8004Reputation {
    struct Feedback {
        address from;
        address to;
        int256 score;
        string[] tags;
        string proofUri;
        uint256 timestamp;
    }

    event FeedbackSubmitted(address indexed from, address indexed to, int256 score, string[] tags);
    event ScoreUpdated(address indexed agent, int256 newScore);

    function submitFeedback(address to, int256 score, string[] calldata tags, string calldata proofUri) external;
    function getScore(address agent) external view returns (int256);
    function getFeedbackCount(address agent) external view returns (uint256);
    function getFeedback(address agent, uint256 index) external view returns (Feedback memory);
}

contracts/interfaces/IERC8004Validation..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IERC8004Validation {
    struct ValidationRecord {
        bytes32 gigId;
        address[] validators;
        uint256 votesFor;
        uint256 votesAgainst;
        bool resolved;
        bool approved;
        uint256 timestamp;
    }

    event ValidationCreated(bytes32 indexed gigId, uint256 threshold);
    event VoteCast(bytes32 indexed gigId, address indexed validator, bool approve);
    event ValidationResolved(bytes32 indexed gigId, bool approved);

    function createValidation(bytes32 gigId, uint256 threshold) external;
    function castVote(bytes32 gigId, bool approve) external;
    function getValidation(bytes32 gigId) external view returns (ValidationRecord memory);
    function isResolved(bytes32 gigId) external view returns (bool);
}

